import * as tomato from "@po-to/tomato";
import * as tdom from "@po-to/tomato-jquery";
import * as project from "views/global/common/Project";
import * as funs from "views/global/common/Funs";
import * as model from "views/global/common/Model";
import * as api from "views/global/common/API";

class VPresenter extends project.VPresenter {
    constructor(view: tomato.VPView, parent?: tomato.VPresenter, vpid?: string) {
        super(view, parent, vpid);
        this._watchEvent();
        this._els = this._getElements();
        this.addListener(tomato.DialogEvent.Closed, function () {
            //关闭按钮退出APP
            // alert("退出APP");
        });
    }
    /**弹出加盟工程商 */
    _evt_openJoinSupplier() {
        tomato.getVPresenter<tomato.VPresenter>(funs.moduleToUrl('m/joinUs/joinSupplierBasicInfo'), (joinSupplierBasicInfo) => {
            funs.loadPage(joinSupplierBasicInfo);
        });
        (this.parent as tomato.Dialog).close();
    }
    /**关闭按钮退出APP */
    _evt_close() {
        (this.parent as tomato.Dialog).close();
        //关闭按钮退出APP
    }
}
export = VPresenter;